E_LENGTH)\r\n			\r\n			iRankPos = get_user_stats(iPlayer, izStats, izBody)\r\n			iRankMax = get_statsnum()\r\n			\r\n			set_hudtype_specmode()\r\n			ShowSyncHudMsg(id, g_HudSync_SpecInfo, \"%L\", id, \"X_RANK_IS\", t_sName, iRankPos, iRankMax)\r\n		}\r\n	}\r\n	\r\n	return PLUGIN_CONTINUE\r\n}\r\n\r\npublic client_connect(id)\r\n{\r\n	if (ShowStats)\r\n	{\r\n		get_user_info(id, \"_amxstatsx\", t_sText, MAX_TEXT_LENGTH)\r\n		g_izStatsSwitch[id] = (t_sText[0]) ? str_to_num(t_sText) : -1\r\n	}\r\n	else\r\n		g_izStatsSwitch[id] = 0\r\n\r\n	g_izKilled[id][KILLED_KILLER_ID] = 0\r\n	g_izKilled[id][KILLED_KILLER_STATSFIX] = 0\r\n	g_izShowStatsFlags[id] = 0		// Clear all flags\r\n	g_fzShowUserStatsTime[id] = 0.0\r\n\r\n	return PLUGIN_CONTINUE\r\n}\r\n\r\nnew iEffect, iColor_R, iColor_G, iColor_B, g_pcvarColor\r\nnew Float:iFadeIn, Float:iFadeOut, Float:iHoldTime, Float:iPos_X, Float:iPos_Y\r\n\r\npublic StatsMarquee(id)\r\n{\r\n	if(!get_pcvar_num(pcvar_statsmarquee))\r\n	{\r\n		return PLUGIN_HANDLED\r\n	}\r\n	\r\n	new izStats[8], izBody[8]\r\n	\r\n	get_stats(marquee_iID, izStats, izBody, m_sName, MAX_NAME_LENGTH)\r\n	\r\n	switch(get_pcvar_num(pcvar_statsmarquee_position))\r\n	{\r\n		case 1:\r\n		{\r\n			iPos_X = 0.7\r\n			iPos_Y = 0.05	\r\n		}\r\n		case 2:\r\n		{\r\n			iPos_X = 0.02\r\n			iPos_Y = 0.7	\r\n		}\r\n		case 3:\r\n		{\r\n			iPos_X = 0.02\r\n			iPos_Y = 0.2	\r\n		}\r\n		default:\r\n		{\r\n			iPos_X = 0.7\r\n			iPos_Y = 0.78\r\n		}\r\n	}\r\n	\r\n	g_pcvarColor = get_pcvar_num(pcvar_statsmarquee_color)\r\n	\r\n	if(g_pcvarColor == 11)\r\n	{\r\n		g_pcvarColor = random_num(1, 9)\r\n	}\r\n	\r\n	switch(g_pcvarColor)\r\n	{\r\n		case 1:\r\n		{\r\n			// RED\r\n			iColor_R = 255\r\n			iColor_G = 0\r\n			iColor_B = 0\r\n		}\r\n		case 2:\r\n		{\r\n			// GREEN\r\n			iColor_R = 0\r\n			iColor_G = 255\r\n			iColor_B = 0\r\n		}\r\n		case 3:\r\n		{\r\n			// BLUE\r\n			iColor_R = 0\r\n			iColor_G = 0\r\n			iColor_B = 255\r\n		}\r\n		case 4:\r\n		{\r\n			// YELLOW\r\n			iColor_R = 255\r\n			iColor_G = 255\r\n			iColor_B = 0\r\n		}\r\n		case 5:\r\n		{\r\n			// CYAN\r\n			iColor_R = 0\r\n			iColor_G = 255\r\n			iColor_B = 255\r\n		}\r\n		case 6:\r\n		{\r\n			// MAGENTA\r\n			iColor_R = 255\r\n			iColor_G = 0\r\n			iColor_B = 255\r\n		}\r\n		case 7:\r\n		{\r\n			// ORANGE\r\n			iColor_R = 255\r\n			iColor_G = 128\r\n			iColor_B = 0\r\n		}\r\n		case 8:\r\n		{\r\n			// VIOLET\r\n			iColor_R = 0\r\n			iColor_G = 128\r\n			iColor_B = 255\r\n		}\r\n		case 9:\r\n		{\r\n			// GRAY\r\n			iColor_R = 100\r\n			iColor_G = 100\r\n			iColor_B = 100\r\n		}\r\n		case 10:\r\n		{\r\n			// RANDOM\r\n			iColor_R = random_num(0, 255)\r\n			iColor_G = random_num(0, 255)\r\n			iColor_B = random_num(0, 255)\r\n		}\r\n		default:\r\n		{\r\n			\r\n		}\r\n	}\r\n	\r\n	if(get_pcvar_num(pcvar_style))\r\n	{\r\n		switch(marquee_iID)\r\n		{\r\n			case 0:\r\n			{\r\n				formatex(marquee_place, charsmax(marquee_place), \"%L\", id, \"MM_FIRSTPLACE\")\r\n				\r\n				if(!g_pcvarColor)\r\n				{\r\n					iColor_R = 0\r\n					iColor_G = 255\r\n					iColor_B = 0\r\n				}\r\n			}\r\n			case 10:\r\n			{\r\n				formatex(marquee_place, charsmax(marquee_place), \"%L\", id, \"MM_SECONDPLACE\")\r\n				\r\n				if(!g_pcvarColor)\r\n				{\r\n					iColor_R = 255\r\n					iColor_G = 255\r\n					iColor_B = 0\r\n				}\r\n			}\r\n			case 20:\r\n			{\r\n				formatex(marquee_place, charsmax(marquee_place), \"%L\", id, \"MM_THIRDPLACE\")\r\n				\r\n				if(!g_pcvarColor)\r\n				{\r\n					iColor_R = 255\r\n					iColor_G = 0\r\n					iColor_B = 0\r\n				}\r\n			}\r\n		}\r\n	}\r\n	else\r\n	{\r\n		formatex(marquee_place, charsmax(marquee_place), \"%L\", id, \"SERVER_STATS\")\r\n	}\r\n	\r\n	\r\n	switch(get_pcvar_num(pcvar_statsmarquee_effect))\r\n	{\r\n		case 1:\r\n		{\r\n			iEffect = 1\r\n			iFadeIn = 0.2\r\n			iFadeOut = 0.2\r\n			iHoldTime = 5.8\r\n		}\r\n		case 2:\r\n		{\r\n			iEffect = 2\r\n			iFadeIn = 0.05\r\n			iFadeOut = 0.5\r\n			iHoldTime = 2.0\r\n		}\r\n		default:\r\n		{\r\n			iEffect = 0\r\n			iFadeIn = 0.5\r\n			iFadeOut = 0.5\r\n			iHoldTime = 5.5\r\n		}\r\n	}\r\n	\r\n	\r\n	set_hudmessage(iColor_R, iColor_G, iColor_B, iPos_X, iPos_Y, iEffect, 0.1, iHoldTime, iFadeIn, iFadeOut, -1)\r\n	show_hudmessage(0,\"%L\", id, \"MM_MARQUEE\" , marquee_place, m_sName, marquee_iID + 1, izStats[0], izStats[1])\r\n	\r\n	marquee_iID++\r\n		\r\n	if(marquee_iID >= 30)\r\n	{\r\n		marquee_iID = 0\r\n		set_task(300.0, \"StatsMarquee\", 0, _, 0)\r\n	}\r\n	else\r\n	{\r\n		set_task(6.0, \"StatsMarquee\", 0, _, 0)\r\n	}\r\n	\r\n	\r\n	\r\n	return PLUGIN_CONTINUE\r\n}\r\n\r\n/*	Thanks for Damaged Soul for finding the information on how to do this.\r\n	http://forums.alliedmods.net/showthread.php?p=79604#post79604\r\n\r\n	This is code snippets I find useful.\r\n\r\n	^x01 is Yellow\r\n	^x03 is Team Color. Ie. Red (Terrorist) or blue (Counter-Terrorist) or grey (SPECTATOR or UNASSIGNED).\r\n	^x04 is Green\r\n\r\n	The colors red, grey, and blue can\'t be used on the same line. This is not possible at all to do.\r\n\r\n	Also there are limitation to using Red or Grey while on the TEAM CT.\r\n	You would have to do for example if they were on CT.\r\n\r\n	ColorChat(0, RED, \"%s, ^x01This color is yellow. ^x03This color is red. ^x04This color is green.);\r\n\r\n	You have to set the Type to RED because of certain messages that need to be sent out so a person on the\r\n	team CT can be sent red colors.\r\n	\r\n	Some important information. When using MSG_ALL to send color message to all clients and your going to use blue/grey/red\r\n	color sending the TeamInfo Message to tempoary change the player team to achieve this. You must send that message in MSG_ALL\r\n	also. If your going to only send it to one person. You need to MSG_ONE on TeamInfo and SayText. (Thanks to CheapSuit I was able\r\n	to see their was an error and fix it).\r\n	\r\n*/\r\n\r\ncolorChat(id, ChatColor:color, const msg[], {Float,Sql,Result,_}:...)\r\n{\r\n	new team, index, MSG_Type\r\n	new bool:teamChanged = false\r\n	static message[192]\r\n	\r\n	switch(color)\r\n	{\r\n		case CHATCOLOR_NORMAL: // Normal\r\n		{\r\n			message[0] = 0x01;\r\n		}\r\n		case CHATCOLOR_GREEN: // Green\r\n		{\r\n			message[0] = 0x04;\r\n		}\r\n		default: // Grey, Red, Blue\r\n		{\r\n			message[0] = 0x03;\r\n		}\r\n	}\r\n	\r\n	vformat(message[1], 190, msg, 4);\r\n	replace_all(message, 190, \"$g\", \"^x04\")\r\n	replace_all(message, 190, \"$n\", \"^x01\")\r\n	replace_all(message, 190, \"$t\", \"^x03\")\r\n		\r\n	if(id == 0)\r\n	{\r\n		index = findAnyPlayer();\r\n		MSG_Type = MSG_ALL;\r\n	}\r\n	else\r\n	{\r\n		index = id;\r\n		MSG_Type = MSG_ONE;\r\n	}\r\n	if(index != 0)\r\n	{\r\n		team = get_user_team(index);	\r\n		if(color == CHATCOLOR_RED && team != 1)\r\n		{\r\n			messageTeamInfo(index, MSG_Type, g_TeamName[1])\r\n			teamChanged = true\r\n		}\r\n		else\r\n		if(color == CHATCOLOR_BLUE && team != 2)\r\n		{\r\n			messageTeamInfo(index, MSG_Type, g_TeamName[2])\r\n			teamChanged = true\r\n		}\r\n		else\r\n		if(color == CHATCOLOR_GREY && team != 0)\r\n		{\r\n			messageTeamInfo(index, MSG_Type, g_TeamName[0])\r\n			teamChanged = true\r\n		}\r\n		messageSayText(index, MSG_Type, message);\r\n		if(teamChanged)\r\n		{\r\n			messageTeamInfo(index, MSG_Type, g_TeamName[team])\r\n		}\r\n	}\r\n}\r\n\r\nmessageSayText(id, type, message[])\r\n{\r\n	message_begin(type, g_msgSayText, _, id)\r\n	write_byte(id)		\r\n	write_string(message)\r\n	message_end()\r\n}\r\n	\r\nmessageTeamInfo(id, type, team[])\r\n{\r\n	message_begin(type, g_msgTeamInfo, _, id)\r\n	write_byte(id)\r\n	write_string(team)\r\n	message_end()\r\n}\r\n	\r\nfindAnyPlayer()\r\n{\r\n	static players[32], inum, pid\r\n	\r\n	get_players(players, inum, \"ch\")\r\n	\r\n	for (new a = 0; a < inum; a++)\r\n	{\r\n		pid = players[a]\r\n		if(is_user_connected(pid))\r\n			return pid\r\n	}\r\n	\r\n	return 0\r\n}\r\n\r\n', '84', 'StatsX Shell (Ultimate StatsX).zip', 'statsx_shell.sma', 'Jos jedan dobar plugin koji koristi mali broj ljudi je statsx_shell\r\nmnogo napredniji od obicnog statsx plugina\r\nIma award sistem, prati koliko je vremena igrac proveo na serveru...\r\nkad je zadnji put posetio server\r\npokazuje hud poruke ko se nalazi u top30\r\nima mogucnost da izbaci rank za svakog 10-og (nisam siguran da li radi i na ostale brojeve)\r\nnpr. /top100, /top50 /top20\r\n\r\nOriginalni Autor: AMX Dev Team\r\nPrepravio: MastaMan\r\n\r\nJezici: EN, UA, RU\r\n\r\nInstalacija:\r\nâ€¢Iskljuciti standardni statsx.amxx koji se nalazi u addons/amxmodx/configs/plugins.ini\r\nâ€¢Otpakovati .rar fajl.\r\nâ€¢Plugin statsx_shell.amxx kopirati u addons/amxmodx/plugins folder koji se nalazi na vasem serveru.\r\nâ€¢Ukucati statsx_shell.amxx u addons/amxmodx/configs/plugins.ini\r\nâ€¢Prevod (language fajl) statsx_shell.txt kopirati u addons/amxmodx/data/lang folder\r\nâ€¢Napraviti novi folder statsx_shell koji ce se nalaziti u addons/amxmodx/configs\r\nâ€¢U folder statsx_shell koji se nalazi u (addons/amxmodx/configs)kopirati statsx_shell.cfg\r\nâ€¢Restartovati server\r\n\r\nChat komande:\r\n/ Top, / top10, / top15, / place, / 1place, / 1place - 10 najboljih igraca na serveru\r\n/ Top2, / top20, / 2place - 20 najboljih igraca na serveru\r\n/ Top3, / top30, / 3place - 30 najboljih igraca na serveru\r\n/ Dmg, / damage - top 10 po najvecoj ucinjenoj steti\r\n/ Hs, / headshot - top 10 igraca koji su napravili najvise headova(headshot!!!)\r\n/ Bot, / bots, / flop, / flops, / noob, / noobs, / lol, / lols - 10 najgorih igraca\r\n/ TopX - pokazuje bilo koji top gde se broj zavrsava na 0 \r\n/ Award - pokazuje statistiku nagrada\r\n/ Day, / attend - pokazuje vreme provedeno na serveru za taj dan\r\n/ Time, / pt - pokazuje statistiku vremena provedenog na serveru\r\n\r\nCvarovi:\r\nSvi cvarovi se nalaze u addons/amxmodx/configs/stastx_shell/statsx_shell.cfg\r\n\r\nu .zip fajlu se nalazi i .amxx i .sma fajl \r\n.CFG fajl u kojem se nalaze cvarovi sam preveo sa ruskog.', '0');
INSERT INTO `plugini` (`plugin`, `naziv`, `kategorija`, `source`, `get_sma`, `get_amxx`, `sma_preuzimanja`, `amxx_preuzimanja`, `verzija`, `autor`, `datum`, `aktivan`, `kod`, `user_id`, `original_amxx`, `original_sma`, `tutorijal`, `banned`) VALUES
(36, 'Anti Bot Flood ', 'Ostalo', 'Free', 'sma-5d445aabcbfd88.78002687.sma', 'amxx-5d445aabcbf717.27006692.amxx', 2, 1, 'v1.0', 'Petardica (5ardica)', '02.08.2019.', 1, '#include <amxmodx>\r\n#include <celltrie>\r\n#define PLUGIN \"floodban\" // \r\n#define VERSION \"0.1\"\r\n#define AUTHOR \"5ardica\"\r\n\r\nnew Trie:g_u_ip_warn\r\nnew Trie:g_u_time\r\n\r\npublic plugin_init()\r\n{\r\n	register_plugin(PLUGIN, VERSION, AUTHOR);\r\n	g_u_ip_warn=TrieCreate();\r\n	g_u_time=TrieCreate();\r\n	set_task(360.0,\"arrclear\",_,_,_,\"b\")\r\n}\r\n\r\npublic arrclear()\r\n{\r\n	TrieClear(g_u_ip_warn)\r\n	TrieClear(g_u_time)\r\n}\r\npublic client_connect(id) \r\n{ \r\n	if(is_user_bot(id)) return;\r\n	new ip[32]\r\n	new ltime\r\n	get_user_ip(id,ip,31,0)\r\n	if(!ip[0]) return;\r\n	if (!TrieKeyExists(g_u_ip_warn, ip))\r\n	{\r\n		TrieSetCell(g_u_ip_warn,ip,1);\r\n	}\r\n	else\r\n	{\r\n		TrieGetCell(g_u_time,ip,ltime);\r\n		if(!(get_systime()-ltime))\r\n		{\r\n			new warn\r\n			TrieGetCell(g_u_ip_warn,ip,warn)\r\n			if(++warn>4)\r\n			{\r\n				new uip[32]\r\n				get_user_ip(id,uip,31,1)\r\n				server_cmd(\"addip 600.0 %s\",uip)\r\n				TrieDeleteKey(g_u_ip_warn, ip);\r\n			}\r\n			else\r\n				TrieSetCell(g_u_ip_warn,ip,warn)\r\n		}\r\n	}\r\n	TrieSetCell(g_u_time,ip,get_systime());\r\n}\r\n', '91', 'floodban.amxx', 'floodban.sma', 'Ovako, \r\n\r\nfloodban.sma ubacujete cstrike>addons>amxmodx>scripting\r\nfloodban.amxx ubacujete cstrike>addons>amxmodx>plugins\r\n\r\n\r\nPosle toga, \r\nUdjete u cstrike>addons>amxmodx>configs>plugins.ini i na kraju tog fajla upisete \r\nu novom redu  floodban.amxx !\r\n\r\nPromenite mapu ili restartujte server i to je to :P\r\n', '0'),
(37, 'OpenGL32 Detektor', 'Ostalo', 'Free', 'sma-5d445fa43aaf53.38248317.sma', 'amxx-5d445fa43aa9a2.45764506.amxx', 0, 0, 'v1.0', '5ardica', '02.08.2019.', 1, '\r\n#include <amxmodx>\r\n#include <amxmisc>\r\n#define PLUGIN \"OpenglDetektor\"\r\n#define VERSION \"1.1\"\r\n#define AUTHOR \"5ardica\"\r\n\r\nnew g_CheckPlayer[33]		\r\nnew g_PunishPlayer[33]\r\n\r\nnew g_File[17] = \"../opengl32.dll\"\r\nnew g_PrecFile[32]\r\nnew g_Path[12] =\"../opengl32\"\r\nnew Array:g_Files\r\n\r\nnew g_Enable\r\nnew g_PunishType\r\nnew g_BanTime\r\nnew g_ShowAdmin\r\n\r\nstock bool:file_copy(SOURCE[], TARGET[], error[], const ERRORLEN, const bool:REPLACE_TARGET = false) \r\n{\r\n	if (!file_exists(SOURCE)) \r\n	{\r\n		format(error, ERRORLEN, \"File copy error: Source ^\"%s^\" doesn\'t exist!\", SOURCE)\r\n		return false\r\n	}\r\n	if (!REPLACE_TARGET && file_exists(TARGET)) \r\n	{\r\n		format(error, ERRORLEN, \"File copy error: Target ^\"%s^\" exists!\", TARGET)\r\n		return false\r\n	}\r\n	\r\n	new source = fopen(SOURCE, \"rb\")\r\n	if (!source) \r\n	{\r\n		format(error, ERRORLEN, \"File copy error: Opening source ^\"%s^\" failed!\", SOURCE)\r\n		return false\r\n	}\r\n	\r\n	new target = fopen(TARGET, \"wb\")\r\n	if (!target)\r\n	{\r\n		format(error, ERRORLEN, \"File copy error: Opening target ^\"%s^\" failed!\", TARGET)\r\n		fclose(source)\r\n		return false\r\n	}\r\n	\r\n	for (new buffer, eof = feof(source); !eof; !eof && fputc(target, buffer)) \r\n	{\r\n		buffer = fgetc(source)\r\n		eof = feof(source)\r\n	}\r\n	\r\n	fclose(source)\r\n	fclose(target)\r\n\r\n	return true\r\n}\r\n\r\npublic plugin_init()\r\n{\r\n	register_plugin(PLUGIN, VERSION, AUTHOR)\r\n	register_concmd(\"amx_opengl_list\",\"opengl_list\",ADMIN_BAN,\"List opengl files and stats\")\r\n	register_concmd(\"amx_opengl_set\",\"opengl_set\",ADMIN_BAN,\"\'opengl_set x\' 1->nr.max. Set specific opengl file for nextmap or 0 for cyclic\")\r\n	register_event(\"Damage\", \"Event_Damage\", \"b\", \"2!0\", \"3=0\", \"4!0\");\r\n	\r\n	g_Enable = register_cvar(\"opengl_enable\", \"1\") // enable?\r\n	g_BanTime = register_cvar(\"opengl_bantime\", \"43200\") // 30 Days BAN (43200 minutes)\r\n	g_PunishType = register_cvar(\"opengl_punish_type\", \"1\") // 1-ban,2-kick,3-log\r\n	g_ShowAdmin = register_cvar(\"opengl_show_admin\", \"1\") // 0-disable,1-anounce admins,2-anounce everybody\r\n	\r\n	if (!file_exists(g_File))\r\n	{\r\n		server_print(\"[OpenglCheck] No opengl32 ,plugin disable!!!\") \r\n		set_pcvar_num(g_Enable,0)\r\n		//return PLUGIN_HANDLED\r\n	}\r\n	\r\n	set_task(1.0,\"load\")\r\n	return PLUGIN_CONTINUE\r\n}\r\n\r\npublic load()\r\n{\r\n	\r\n	if (!dir_exists(g_Path))\r\n         {\r\n		server_print(\"[OpenglCheck] No dir opengl32 with source files!!!\") \r\n		return PLUGIN_HANDLED\r\n	}\r\n\r\n	//check and copy opengl file in case of first time run plugin\r\n	new glfile[32], glfile_path[64], err[64]\r\n	\r\n	if(!file_exists(g_File))\r\n	{\r\n		new gldir = open_dir(g_Path,glfile,31)\r\n		\r\n		formatex(glfile_path,63,\"%s/%s\",g_Path,glfile)\r\n		file_copy(glfile_path,g_File,err,63,true)\r\n		\r\n		if (strlen(err)>0)\r\n		{\r\n			server_print(\"[OpenglCheck] %s\",err)\r\n			server_print(\"[OpenglCheck] Invalid opengl32 file,!!!\")\r\n			return PLUGIN_HANDLED\r\n		}\r\n		close_dir(gldir)\r\n	}\r\n	\r\n	//Loop opengl dir to identify curent opengl file with md5, \r\n	//and store names for setting next precache file\r\n	\r\n	new prec_buff[34], glfile_buff[34]\r\n	md5_file(g_File,prec_buff)\r\n	\r\n	g_Files = ArrayCreate(32);\r\n	new gldir = open_dir(g_Path,glfile,31)\r\n	new maxarr = 0\r\n	\r\n	do\r\n	{\r\n		if(strlen(glfile)>3)\r\n		{\r\n			if(contain(glfile,\"@\")!=-1) \r\n			{\r\n				new renfile[64]\r\n				\r\n				formatex(renfile,63,\"%s\",glfile)\r\n				replace(glfile,31,\"@\",\"\")\r\n				formatex(glfile_path,63,\"%s/%s\",g_Path,glfile)\r\n				format(renfile,63,\"%s/%s\",g_Path,renfile)\r\n				\r\n				rename_file(renfile,glfile_path,true)\r\n				server_print(\"[OpenglCheck] Invalid opengl file name: %s renamed to: %s\",renfile,glfile_path)\r\n			}\r\n			\r\n			formatex(glfile_path,63,\"%s/%s\",g_Path,glfile)\r\n			md5_file(glfile_path,glfile_buff)\r\n			if(equal(prec_buff,glfile_buff))\r\n			{\r\n				copy(g_PrecFile,31,glfile)\r\n				server_print(\"[OpenglCheck] Precached opengl file is %s\",g_PrecFile) 	\r\n			}\r\n			ArrayPushString(g_Files,glfile);\r\n			maxarr += 1;\r\n		}\r\n	\r\n	}\r\n	while ( next_file(gldir,glfile,31))\r\n	close_dir(gldir)		\r\n	\r\n	//check and update list of files\r\n	new listfile[128]\r\n	\r\n	get_configsdir(listfile,127)\r\n	format(listfile,127,\"%s/openglfiles.txt\",listfile)\r\n	\r\n	if(!file_exists(listfile))\r\n	{\r\n		server_print(\"[OpenglCheck] openglfiles.txt not exist, create file !!!\")\r\n		write_file(listfile,g_PrecFile,0)\r\n		write_file(listfile,\"0\",1)\r\n	}\r\n\r\n	new i, chk, lstfile[36],chkfile[36],arrfile[32], maxlst, len\r\n	\r\n	//new maxlst = file_size(listfile,1)\r\n\r\n	for(i = 0 ; i < maxarr ; i++)\r\n	{\r\n		ArrayGetString(g_Files,i,arrfile,31)\r\n		formatex(chkfile,35,\"@%s\",arrfile)\r\n		chk = 0\r\n		\r\n		\r\n		/*\r\n		new listfile_handle = fopen( listfile, \"rt\" );\r\n		while(!feof(listfile_handle))\r\n		{\r\n			fgets(listfile_handle,lstfile,35)\r\n			if(strlen(lstfile) >10)\r\n			{\r\n				if (strfind(lstfile,chkfile)!=-1)\r\n					chk += 1\r\n			}\r\n		}\r\n		*/\r\n		\r\n		new j\r\n		maxlst = file_size(listfile,1)\r\n		\r\n		for(j = 2 ; j < maxlst-1 ; j++)\r\n		{\r\n			read_file(listfile,j,lstfile,31,len)\r\n			if (strfind(lstfile,chkfile)!=-1)\r\n				chk += 1\r\n		}\r\n		\r\n		if (chk == 0)	\r\n		{\r\n			server_print(\"[OpenglCheck] write opengl file %s in list\",chkfile)\r\n			format(chkfile,35,\"0%s\",chkfile)\r\n			write_file(listfile,chkfile,-1)\r\n		}\r\n	}\r\n	\r\n	//set nextopengl file\r\n	new nextfile[32], setfile[32], pos\r\n	\r\n	read_file(listfile,0,nextfile,31,len)\r\n	read_file(listfile,1,setfile,31,len)\r\n	\r\n	if( strlen(setfile) < 2 ) //cycle ?\r\n	{\r\n		\r\n		if(maxlst > 3) \r\n		{\r\n			i=0\r\n			for( i = 0 ; i < maxarr ; i++ )\r\n			{\r\n				ArrayGetString(g_Files,i,chkfile,31)\r\n				\r\n				if ( strfind(nextfile,chkfile) != -1)\r\n				{\r\n					if (i == maxarr-1)\r\n						pos = 0\r\n					else\r\n						pos = i+1\r\n				}\r\n			}\r\n			ArrayGetString(g_Files,pos,nextfile,31)\r\n		}\r\n		else\r\n		{\r\n			copy(nextfile,31,g_PrecFile)\r\n		}\r\n		\r\n		formatex(glfile_path,63,\"%s/%s\",g_Path,nextfile)\r\n		write_file(listfile,nextfile,0)\r\n	}\r\n	else\r\n	{\r\n		formatex(glfile_path,63,\"%s/%s\",g_Path,setfile)\r\n	}\r\n	\r\n	ArrayDestroy(g_Files);\r\n	\r\n	file_copy(glfile_path,g_File,err,63,true)\r\n	if (strlen(err)>0)\r\n		server_print(\"[OpenglCheck] %s\",err)\r\n	return PLUGIN_CONTINUE\r\n}\r\n\r\npublic plugin_precache()\r\n{\r\n    force_unmodified(force_exactfile, {0,0,0},{0,0,0}, g_File)\r\n}\r\n\r\npublic inconsistent_file(id, const filename[], reason[64])\r\n{ \r\n	if (equal(filename,g_File))\r\n	{\r\n		g_CheckPlayer[id]=1\r\n		g_PunishPlayer[id]=0\r\n	}\r\n	return PLUGIN_HANDLED \r\n} \r\n\r\npublic client_connect(id)\r\n{\r\n	g_CheckPlayer[id]=0\r\n	g_PunishPlayer[id]=1\r\n	if((get_pcvar_num(g_Enable)==0)&&(file_exists (g_File)))\r\n		set_pcvar_num(g_Enable,1)\r\n	return PLUGIN_CONTINUE\r\n}\r\n\r\npublic client_putinserver(id)\r\n{\r\n	if ( is_user_hltv(id) || is_user_bot(id) )\r\n	{\r\n		g_PunishPlayer[id]=0\r\n		g_CheckPlayer[id]=1\r\n		return PLUGIN_HANDLED\r\n	}\r\n	set_task(5.0, \"check_file\", id)\r\n	return PLUGIN_HANDLED\r\n}\r\n\r\npublic client_disconnect(id)\r\n{\r\n	g_CheckPlayer[id]=0\r\n	g_PunishPlayer[id]=0\r\n	if(task_exists(id))\r\n		remove_task(id)\r\n}  \r\n\r\npublic Event_Damage(id) \r\n{\r\n	if(get_pcvar_num(g_Enable)==0)\r\n		return PLUGIN_HANDLED\r\n	if (id>0) {\r\n		new attacker = get_user_attacker(id)\r\n		if (attacker > 0 && attacker <= 32)\r\n		{ \r\n			if(g_PunishPlayer[attacker]==0)\r\n				return PLUGIN_HANDLED\r\n			if(g_CheckPlayer[attacker]==0)\r\n				set_task(1.0,\"punish_player\",attacker+33)\r\n		}\r\n	}\r\n	return PLUGIN_CONTINUE;\r\n}\r\n\r\npublic check_file(id)\r\n{\r\n	if(get_pcvar_num(g_Enable)==0)\r\n		return PLUGIN_HANDLED\r\n	\r\n	\r\n	if(g_CheckPlayer[id]==0)\r\n	{   \r\n		new name[32]\r\n		get_user_name(id, name, sizeof(name)-1)\r\n		\r\n		new msg[127]\r\n		switch(get_pcvar_num(g_PunishType))\r\n		{\r\n			case 1:\r\n				formatex(msg,127,\"; ce biti banovan na %d min. posle prvog napada\", get_pcvar_num(g_BanTime) )\r\n			case 2:\r\n				formatex(msg,127,\"; ce biti kikovan posle prvog napada\")\r\n			case 3:\r\n				formatex(msg,127,\"!!!\")\r\n			default:\r\n				formatex(msg,127,\"; ce biti banovan na %d min. posle prvog napada\", get_pcvar_num(g_BanTime) )\r\n		}\r\n		format(msg,127,\"[OpenglCheck] Igrac %s ima OpenGL32 hack fajl. %s\",name,msg)\r\n\r\n		switch(get_pcvar_num(g_ShowAdmin))\r\n		{\r\n			case 0:\r\n				return PLUGIN_HANDLED\r\n			case 1:\r\n			{\r\n				new players[32],num,i;\r\n				get_players(players,num,\"c\");\r\n				for(i=1;i<num;i++) \r\n				{\r\n					if( get_user_flags(i) & ADMIN_KICK )\r\n						client_print(i,print_chat,\" %s\",msg);\r\n				}\r\n			}\r\n			case 2:\r\n				client_print(0,print_chat,\" %s\",msg);\r\n			default:\r\n				client_print(0,print_chat,\" %s\",msg);\r\n		}\r\n	}\r\n	return PLUGIN_CONTINUE;\r\n}\r\n\r\npublic punish_player(id)\r\n{\r\n	\r\n	if(get_pcvar_num(g_Enable)==0)\r\n		return PLUGIN_HANDLED\r\n		\r\n	new name[32], authid[32], ip[32]\r\n	id -= 33\r\n	get_user_authid(id, authid, sizeof(authid)-1)\r\n	get_user_ip(id, ip, 31, 1)\r\n	get_user_name(id, name, sizeof(name)-1)\r\n	\r\n	client_print(id,print_chat,\"[OpenglCheck]OpenGL32 hak detektovan na tebi :P\") //\r\n	\r\n	switch(get_pcvar_num(g_PunishType))\r\n	{\r\n		case 1:\r\n			server_cmd(\"amx_ban %s %d ^\"Wallhack OpenGL^\"\", name, get_pcvar_num(g_BanTime) )\r\n		case 2:\r\n			server_cmd(\"kick %s  Wallhack OpenGL\", name) \r\n		default:\r\n			server_cmd(\"amx_ban %s %d ^\"Wallhack OpenGL^\"\", name, get_pcvar_num(g_BanTime) )\r\n	}\r\n				\r\n	log_amx(\"Wallhack OpenGL verzija %s je pronadjena %s<%s><%s> \",g_PrecFile, name, ip, authid)\r\n	g_PunishPlayer[id]=0\r\n	set_pcvar_num(g_Enable,0) \r\n				\r\n	//increment number in opengl list file\r\n				\r\n	new listfile[128]\r\n	get_configsdir(listfile,127)\r\n	format(listfile,127,\"%s/openglfiles.txt\",listfile)\r\n			\r\n	new upfile[36]\r\n	formatex(upfile,35,\"@%s\",g_PrecFile)\r\n				\r\n	if(!file_exists(listfile)) \r\n	{\r\n		server_print(\"[OpenglCheck] Lista ce se kreirati !!!!\")\r\n		\r\n		write_file(listfile,g_PrecFile,0)\r\n		write_file(listfile,\"0\",1)\r\n		\r\n		format(upfile,35,\"1%s\",upfile)\r\n		write_file(listfile,upfile,2)\r\n		\r\n		return PLUGIN_HANDLED;\r\n	}\r\n				\r\n	//loop list file to find and extract number for precached file\r\n	new maxlst = file_size(listfile,1)\r\n	new i,chkfile[36], pnumstr[5], pnum, pos, len\r\n			\r\n	for( i = 2 ; i < maxlst - 1 ; i++)\r\n	{\r\n		read_file(listfile,i,chkfile,35,len)\r\n					\r\n		if(strfind(chkfile,upfile) != -1 )\r\n		{\r\n			strtok(chkfile,pnumstr,4,chkfile,31,\'@\')\r\n			pnum=str_to_num(pnumstr)\r\n			pnum += 1\r\n			format(upfile,35,\"%d%s\",pnum,upfile)\r\n			pos = i\r\n		}\r\n	}\r\n	if(pos)	//be sure to avoid damage file\r\n		write_file(listfile,upfile,pos)\r\n	return PLUGIN_HANDLED;\r\n}\r\n\r\n\r\n\r\npublic opengl_list(id, level, cid)\r\n{\r\n	if (!cmd_access(id, level, cid, 1))\r\n		return PLUGIN_HANDLED;\r\n	\r\n	new listfile[128]\r\n	get_configsdir(listfile,127)\r\n	formatex(listfile,127,\"%s/openglfiles.txt\",listfile)\r\n	\r\n	new maxlst = file_size(listfile,1)\r\n	new i, name[36], chkfile[64], pnum[5],len\r\n	\r\n	console_print(id,\"---- Status of opengl files ----\")\r\n	console_print(id,\"Current opengl file is %s\",g_PrecFile)\r\n\r\n	read_file(listfile,0,name,35,len)\r\n	console_print(id,\"Next opengl file is %s\",name)\r\n	\r\n	read_file(listfile,1,name,35,len)\r\n	\r\n	if(strlen(name)<5)\r\n		console_print(id,\"ON\")\r\n	else\r\n		console_print(id,\"OFF : %s\",name)\r\n	\r\n	for( i = 2 ; i < maxlst - 1 ; i++)\r\n	{\r\n		read_file(listfile,i,name,35,len)\r\n		strtok(name,pnum,4,name,31,\'@\')\r\n		format(chkfile,63,\"%s/%s\",g_Path,name)\r\n		\r\n		if(file_exists(chkfile))\r\n			console_print(id,\"%d. %s pronadjeno na  %s players\",i-1,name,pnum)\r\n		else\r\n			console_print(id,\"%d. %s je pronadjen na %s players * izbrisan fajl\",i-1,name,pnum)\r\n	}\r\n	return PLUGIN_CONTINUE;\r\n}\r\n\r\npublic opengl_set(id, level, cid)\r\n{\r\n	if (!cmd_access(id, level, cid, 1))\r\n		return PLUGIN_HANDLED;\r\n	\r\n	new listfile[128]\r\n	\r\n	get_configsdir(listfile,127)\r\n	formatex(listfile,127,\"%s/openglfiles.txt\",listfile)\r\n	\r\n	\r\n	if(!file_exists(listfile))\r\n	{\r\n		console_print(id,\"[OpenglCheck] Lista OPENGL32 Fajlova ne postoji bice kreirana \")\r\n		write_file(listfile,g_PrecFile,0)\r\n		write_file(listfile,\"0\",1)\r\n		return PLUGIN_HANDLED;\r\n	}\r\n	\r\n	new arg[4];\r\n	read_argv(1,arg,3);\r\n	\r\n	new lstmax = file_size(listfile,1)\r\n	\r\n	new pos = str_to_num(arg)\r\n	\r\n	if ( pos ==0 )\r\n	{\r\n		console_print(id,\"[OpenglCheck] odabrao si\")\r\n		write_file(listfile,\"0\",1)\r\n		return PLUGIN_HANDLED;\r\n	}\r\n		\r\n	if ( (pos < 0) || ( pos > lstmax - 1 ) )\r\n	{\r\n		console_print(id,\"[OpenglCheck] Los broj fajla\")\r\n		return PLUGIN_HANDLED;\r\n	}\r\n	\r\n	new name[36], chkfile[64], pnum[5], len\r\n	\r\n	read_file(listfile,pos+1,name,35,len)\r\n	strtok(name,pnum,4,name,31,\'@\')\r\n	\r\n	//check if file was not deleted from opengl32 dir\r\n	format(chkfile,63,\"%s/%s\",g_Path,name)\r\n	\r\n	if(!file_exists(chkfile))\r\n	{\r\n		console_print(id,\"[OpenglCheck] Los broj fajla je izbrisan\")\r\n		return PLUGIN_HANDLED;\r\n	}\r\n	\r\n	write_file(listfile,name,1)\r\n	console_print(id,\"[OpenglCheck]Izabrao si %d da bude sledeci loadovan %s\",pos,name)\r\n	\r\n	set_task(1.0,\"load\")\r\n	return PLUGIN_CONTINUE;\r\n}\r\n/* AMXX-Studio Notes -  NE DIRAJ OVDE ISPOD\r\n*{\\\\ rtf1\\\\ ansi\\\\ ansic